var http = require('http');
var redi = require('./');

redi('^/post/(\\d+)$', function (req, res, post) {
  res.end('post ' + post);
});

redi('^/post/(\\d+)/comment/(\\d+)$', function (req, res, post, comment) {
  res.end('comment ' + comment + ' for post ' + post);
});

redi(/.*/, function (req, res) {
  res.end('default');
});

http.createServer(redi).listen(8000);
